import React, {useState, createContext, useEffect} from 'react';
import { Provider } from 'react-native-paper';
import { NavigationContainer, createSwitchNavigator, createAppContainer, createNavigatorFactory } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { MaterialCommunityIcons, MaterialIcons } from '@expo/vector-icons';
import firebase from 'firebase/app';

import Amplify from 'aws-amplify'
import awsconfig from './src/aws-exports'
Amplify.configure(awsconfig)

import 'firebase/storage';
import 'firebase/auth';
import 'firebase/firestore';

import StartScreen from './screens/StartScreen.js'
import LoginScreen from './screens/LoginScreen.js'
import RegisterScreen from './screens/RegisterScreen.js'

import Dashboard from './screens/Dashboard.js'
import MessagesScreen from './screens/MessagesScreen.js'
import SettingsScreen from './screens/SettingsScreen.js'
import ImageScreen from './screens/ImageScreen.js'
import SwipeScreen from './screens/SwipeScreen.js'

const AuthContext = createContext(null)
// const [initialState, setInitialState] = useState();
const PERSISTENCE_KEY = 'NAVIGATION_STATE';

function AuthNavigator() {
  const [user, setUser] = useState(null)

  function login(a) {
    setUser(a)
    const userRef = store.collection('users').doc(a.email);
    console.log("EMAIL", a.email)

    userRef.get().then((doc) => {
      if (doc.exists){
        console.log("Login")
      } else {
        console.log("Sign UP")
        if (a.picture) {
          userRef.set({
            name: a.name || "",
            email: a.email || "",
            picture: a.picture.data.url,
            likes: ["blank"],
            dislikes: ["blank"],
            conversations: [],
          })
        } else {
          userRef.set({
            name: a.name || "",
            email: a.email || "",
            picture: "https://www.edmundsgovtech.com/wp-content/uploads/2020/01/default-picture_0_0.png",
            likes: ["blank"],
            dislikes: ["blank"],
            conversations: [],
          })
        }
      }
    })

  }

  function logout() {
    firebase.auth().signOut()
    setUser(null)
  }

  function isUser() {
    firebase.auth().onAuthStateChanged(function(user) {
      if (user) {
        console.log("IS USER")
        return user
      } else {
        return false
      }
    });
  }

    return user ? (
    <MyTabs 
      user={user} 
      logout={logout}
      onStateChange={(state) =>
        console.log("CHANGED", state)
      }
     />
  ) : (
    <MyStack login={login} />
  )
}

const AuthStack = createStackNavigator();

function MyStack(props) {

  return (
    <NavigationContainer>
      <AuthStack.Navigator screenOptions={{
        headerShown: false
        }}>
        <AuthStack.Screen name="StartScreen" component={StartScreen} options={{ title: 'TalkLater' }} initialParams={{login: props.login}} />
        <AuthStack.Screen name="LoginScreen" component={LoginScreen} initialParams={{login: props.login}} />
        <AuthStack.Screen name="RegisterScreen" component={RegisterScreen} initialParams={{login: props.login}} />
      </AuthStack.Navigator>
    </NavigationContainer>
  );
}

const AppStack = createBottomTabNavigator();

function MyTabs(props) {

  return (
    <NavigationContainer>
      <AppStack.Navigator
        initialRouteName="Swipe"
        tabBarOptions={{
          style: {
            padding: 10
          }
        }}
      >

        <AppStack.Screen
          name="Swipe"
          component={SwipeScreen}
          initialParams={{user: props.user}}
          options={{
            tabBarLabel: '',
            tabBarIcon: ({ color }) => (
              <MaterialCommunityIcons name="home" color={color} size={26} />
            ),
          }}
        />
        <AppStack.Screen
          name="Messages"
          component={MessagesScreen}
          initialParams={{user: props.user}}
          options={{
            tabBarLabel: '',
            tabBarIcon: ({ color }) => (
              <MaterialCommunityIcons name="chat" color={color} size={26} />
            ),
          }}
        />
        <AppStack.Screen
          name="Dashboard"
          component={Dashboard}
          initialParams={{user: props.user}}
          options={{
            tabBarLabel: '',
            tabBarIcon: ({ color }) => (
              <MaterialCommunityIcons name="account-circle" color={color} size={26} />
            ),
          }}
        />
        <AppStack.Screen
          name="Settings"
          component={SettingsScreen}
          initialParams={{user: props.user, logout: props.logout}}
          options={{
            tabBarButton: () => null,
          }}
        />
        <AppStack.Screen
          name="Image"
          component={ImageScreen}
          initialParams={{user: props.user, logout: props.logout}}
          options={{
            tabBarButton: () => null,
          }}
        />
      </AppStack.Navigator>
    </NavigationContainer>
  );
}

var firebaseConfig = {
  apiKey: "AIzaSyDLeiyd8iai6akLcumpP5-A1yxs7t5wflk",
  authDomain: "socially-b729a.firebaseapp.com",
  databaseURL: "https://socially-b729a-default-rtdb.firebaseio.com",
  projectId: "socially-b729a",
  storageBucket: "socially-b729a.appspot.com",
  messagingSenderId: "804187430311",
  appId: "1:804187430311:web:6dad7a05a011fb3a032a82",
  measurementId: "G-P1NKXT7943"
};

// Initialize Firebase
firebase.initializeApp(firebaseConfig);
firebase.firestore().settings( { timestampsInSnapshots: true })
// firebase.analytics();

const storage = firebase.storage();
const store = firebase.firestore();
const authenticate = firebase.auth();

console.log("DATABASE INIT")
var matchRef = store.collection("users")

// check if matching likes
matchRef.onSnapshot('value', snap => {
  console.log(snap, "CHANGED")
})

export  {
   storage, store, authenticate, AuthNavigator as default
 }
